import React, { Component } from 'react';
import './css/App.css';
import { Nav } from './components/Nav';
import { About } from './components/About';
import { Email } from './components/Email';
import { Experiment } from './components/Experiment';
import { LandingPages } from './components/LandingPages';
import { Animation } from './components/Animation';
import ReactPageScroller from "react-page-scroller";


class App extends Component {
  state = {
    currentPage: 1
  }

   goToPage = (e) => {
        console.log('this is the event key' + e.target.id)
        this.reactPageScroller.goToPage(e.currentTarget.id);
    };

  pageOnChange = (number) => {
    var num = parseInt(number);
    this.setState({currentPage: num});
  };

  render() {
    console.log('Current Page: ' + this.state.currentPage);

    return (
      <div className="App-wrapper">
          <div className="navigation">
          <Nav onClick={this.goToPage} />
          </div>
        <div className="page-content">
            <ReactPageScroller animationTimer={900} ref={c => this.reactPageScroller = c} pageOnChange={this.pageOnChange}>
              <About />
              <Email />
              <LandingPages />
              <Experiment />
              <Animation />
            </ReactPageScroller>
          </div>
       </div>
    );
  }
}

export default App;
