import React from 'react';
import mockupdesktop from '../assets/email-desktop.png';
import mockupmobile from '../assets/email-mobile.png';

export class Email extends React.Component {
    state = {
        currentProject: 1
    }

    changeProject = (event) => {
        let newProject = event.target.id;
        let getTabs = document.querySelectorAll('tab-circle');
        for ( var i = 0; i < getTabs.length; i++) {
            console.log('trying to remove' + getTabs[i]);
            getTabs[i].classList.remove('active');
        }
        // let clickedTab = event.target;
        // clickedTab.classList.add('active');
        console.log(getTabs);
        console.log(event.target);
        this.setState({currentProject: newProject})
    }

    render() {
        return (
            <div className="view" id="view-email">
                <div className="view-mockups">
                    <img src={mockupdesktop} className="mockup-desktop" alt="email-template" />
                    <img src={mockupmobile} className="mobile-desktop" alt="email-template" />
                </div>
                <div className="view-description">
                <h1>We do email</h1>
                <p>You can call it magic, but in reality there is no magic wand that can resolve our problems. The solution rests with our team of Creative Wizardsâ€™s hard work and discipline. They have developed a 3-step approach to systematically produce great design every single time.</p>
                <div className="tab-row">
                    <div id="1" className="tab-circle active" onClick={this.changeProject}></div>
                    <div id="2" className="tab-circle" onClick={this.changeProject}></div>
                    <div id="3" className="tab-circle" onClick={this.changeProject}></div>
                </div>
                </div>
            </div>
        )
    }
}
